import React, { Component } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';

class TodoApp extends Component {
    render() {
        return (
            <div className="TodoApp">
                <Router>
                    <>
                        <Route path="/" exact component={LoginComponent} />
                        <Route path="/login" component={LoginComponent} />
                        <Route path="/welcome" component={WelcomeComponent} />
                    </>
                </Router>

                {/* <LoginComponent />
                <WelcomeComponent /> */}
            </div>
        );
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <div>Welcome Meddi</div>
        )
    }
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'meddi',
            password: '',
            hasLoginFailed: false,
            showSuccesMessage: false,
        }
        // this.handlerUserChange = this.handlerUserChange.bind(this)
        // this.handlerPassChange = this.handlerPassChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    // handlerUserChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ username: event.target.value })

    // }

    // handlerPassChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ password: event.target.value })
    // }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    loginClicked() {
        if (this.state.username === 'meddi' && this.state.password === 'dummy') {
            this.props.history.push("/welcome")
            // this.setState({ showSuccesMessage: true })
            // this.setState({ hasLoginFailed: false })
        }

        else {
            console.log('Fail')
            this.setState({ showSuccesMessage: false })
            this.setState({ hasLoginFailed: true })
        }



    }

    render() {
        return (
            <div>
                {/* <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed} /> */}
                {this.state.hasLoginFailed && <div>Invalid Credentials</div>}
                {this.state.showSuccesMessage && <div>Login Sucessful</div>}
                {/* <ShowLoginSuccesMessage showSuccesMessage={this.state.showSuccesMessage} /> */}
                User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                <button onClick={this.loginClicked}>Login</button>
            </div>
        );
    }
}

// function ShowInvalidCredentials(props) {
//     if (props.hasLoginFailed) {
//         return <div>Invalid Credentials</div>
//     }
//     return null;
// }

// function ShowLoginSuccesMessage(props) {
//     if (props.showSuccesMessage) {
//         return <div>Login Sucessful</div>
//     }
//     return null;
// }

export default TodoApp;
