import React, { Component } from 'react';

class TodoApp extends Component {
    render() {
        return (
            <div className="TodoApp">
                <LoginComponent />
            </div>
        );
    }
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'meddi',
            password: ''
        }
        // this.handlerUserChange = this.handlerUserChange.bind(this)
        // this.handlerPassChange = this.handlerPassChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
    }

    // handlerUserChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ username: event.target.value })

    // }

    // handlerPassChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ password: event.target.value })
    // }

    handleChange(event) {
        console.log(this.state);
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    render() {
        return (
            <div>
                User Name: <input type="text" name="username" value={this.state.username} onChange={this.state.username} />
                Password: <input type="password" name="password" value={this.state.password} onChange={this.state.password} />
                <button>Login</button>
            </div>
        );
    }
}

export default TodoApp;
