import React, { Component } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';

class TodoApp extends Component {
    render() {
        return (
            <div className="TodoApp">
                <Router>
                    <HeaderComponent />
                    <Switch>
                        <Route path="/" exact component={LoginComponent} />
                        <Route path="/login" component={LoginComponent} />
                        <Route path="/welcome/:name" component={WelcomeComponent} />
                        <Route path="/todos" component={ListTodosComponent} />
                        <Route path="/logout" component={LogoutComponent} />
                        <Route component={ErrorComponent} />
                    </Switch>
                    <FooterComponent />

                </Router>

                {/* <LoginComponent />
                <WelcomeComponent /> */}
            </div>
        );
    }
}

class FooterComponent extends Component {
    render() {
        return (
            <footer className="footer">
                <span className="text-muted">All Right Reserved 2021 @Meddi</span>
            </footer>
        )
    }
}

class HeaderComponent extends Component {
    render() {
        return (
            <header>
                <nav className="navbar navbar-expand-md navbar-dark bg-dark">
                    <div><a href="#" className="navbar-brand">Meddi</a></div>
                    <ul className="navbar-nav">
                        <li><Link to="/welcome/Meddi" className="nav-link">Home</Link></li>
                        <li><Link to="/todos" className="nav-link">Todos</Link></li>
                    </ul>
                    <ul className="navbar-nav navbar-collapse justify-content-end">
                        <li><Link to="/login" className="nav-link">Login</Link></li>
                        <li><Link to="/logout" className="nav-link">Logout</Link></li>
                    </ul>
                </nav>
            </header>

        )
    }
}

class LogoutComponent extends Component {
    render() {
        return (
            <div>
                <h1>You are logged out</h1>
                <div className="container">
                    Thank you for using our application
                </div>
            </div>
        )
    }
}


class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [
                { id: 1, description: 'Learn React', done: false, targetDate: new Date() },
                { id: 2, description: 'Learn to dance', done: false, targetDate: new Date() },
                { id: 3, description: 'Learn props', done: false, targetDate: new Date() },
            ]
        }
    }
    render() {
        return (

            <div>
                <h1>List Todos</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>id</th>
                                <th>description</th>
                                <th>Is Completed ?</th>
                                <th>Target Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo =>
                                        <tr>
                                            <td>{todo.id}</td>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

class WelcomeComponent extends Component {
    render() {
        return (
            <>
                <h1>Welcome !</h1>
                <div className="container">
                    Welcome {this.props.match.params.name}. You can manage your todos <Link to="/todos">here</Link>.
                </div>
            </>


        )
    }
}

function ErrorComponent() {
    return <div>An Error Occurred. I don't know what to do!!</div>
}

class LoginComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            username: 'meddi',
            password: '',
            hasLoginFailed: false,
            showSuccesMessage: false,
        }
        // this.handlerUserChange = this.handlerUserChange.bind(this)
        // this.handlerPassChange = this.handlerPassChange.bind(this)
        this.handleChange = this.handleChange.bind(this)
        this.loginClicked = this.loginClicked.bind(this)
    }

    // handlerUserChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ username: event.target.value })

    // }

    // handlerPassChange(event) {
    //     console.log(event.target.value);
    //     this.setState({ password: event.target.value })
    // }

    handleChange(event) {
        this.setState(
            {
                [event.target.name]
                    : event.target.value
            }
        )
    }

    loginClicked() {
        if (this.state.username === 'meddi' && this.state.password === 'dummy') {
            this.props.history.push(`/welcome/${this.state.username}`)
            // this.setState({ showSuccesMessage: true })
            // this.setState({ hasLoginFailed: false })
        }

        else {
            console.log('Fail')
            this.setState({ showSuccesMessage: false })
            this.setState({ hasLoginFailed: true })
        }



    }

    render() {
        return (
            <div>
                <h1>Login</h1>
                <div className="container">
                    {/* <ShowInvalidCredentials hasLoginFailed={this.state.hasLoginFailed} /> */}
                    {this.state.hasLoginFailed && <div className="alert alert-warning">Invalid Credentials</div>}
                    {/* {this.state.showSuccesMessage && <div>Login Sucessful</div>} */}
                    {/* <ShowLoginSuccesMessage showSuccesMessage={this.state.showSuccesMessage} /> */}
                    User Name: <input type="text" name="username" value={this.state.username} onChange={this.handleChange} />
                    Password: <input type="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <button className="btn btn-success" onClick={this.loginClicked}>Login</button>
                </div>
            </div>
        );
    }
}

// function ShowInvalidCredentials(props) {
//     if (props.hasLoginFailed) {
//         return <div>Invalid Credentials</div>
//     }
//     return null;
// }

// function ShowLoginSuccesMessage(props) {
//     if (props.showSuccesMessage) {
//         return <div>Login Sucessful</div>
//     }
//     return null;
// }

export default TodoApp;
