import React, { Component } from 'react';
import TodoDataService from '../../api/todo/TodoDataService.js';
import AuthentificationService from './AuthentificationService.js';

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos:
                [
                    // { id: 1, description: 'Learn React', done: false, targetDate: new Date() },
                    // { id: 2, description: 'Learn to dance', done: false, targetDate: new Date() },
                    // { id: 3, description: 'Learn props', done: false, targetDate: new Date() },
                ]
        }
    }

    componentDidMount() {
        let username = AuthentificationService.getLoggedInUserName
        TodoDataService.retrieveAllTodos(username)
            .then(
                response => {
                    console.log(response);
                }
            )

    }

    render() {
        return (

            <div>
                <h1>List Todos</h1>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Is Completed ?</th>
                                <th>Target Date</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo =>
                                        <tr key={todo.id}>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListTodosComponent
