import React, { Component } from 'react';
import './Counter.css';
import PropTypes from 'prop-types';

class Counter extends Component {

    constructor() {
        super();
        this.state = {
            counter: 0
        }
        this.increment = this.increment.bind(this);
    }

    render() {
        return (
            <div>
                <div className="counter">
                    <CounterButton by={1} incrementMethod={this.increment} />
                    <CounterButton by={5} incrementMethod={this.increment} />
                    <CounterButton by={10} incrementMethod={this.increment} />


                </div>
                <div className="counter">

                    <CounterButton by2={1} incrementMethod={this.decrement} />

                </div>
                <span className="count">{this.state.counter}</span>
            </div>
        );
    }

    increment = (by) =>
        this.setState((prevState) => {
            return { counter: prevState.counter + by }
        });

    decrement = (by2) =>
        this.setState((prevState) => {
            return { counter: prevState.counter - by2 }
        });
}


class CounterButton extends Component {

    constructor() {
        super();
        this.state = {
            counter: 0
        }
        this.increment = this.increment.bind(this);
    }

    render = () => {
        return (
            <div>
                <div className="counter">
                    <button onClick={this.increment}>+{this.props.by}</button>
                    {/* <span className="count">{this.state.counter}</span> */}
                </div>
                <div className="unCounter">
                    <button onClick={this.decrement}>-{this.props.by2}</button>
                </div>
            </div>
        )
    }
    increment = (by) => {
        this.setState((prevState) => {
            return { counter: this.state.counter + by }
        });

        this.props.incrementMethod(this.props.by);
    }
    decrement = (by2) => {
        this.setState((prevState) => {
            return { counter: this.state.counter - by2 }
        });

        this.props.incrementMethod(this.props.by);
    }
}

CounterButton.defaultProps = {
    by: 1
}

CounterButton.propTypes = {
    by: PropTypes.number
}

export default Counter;
