import React, { Component } from 'react';
import TodoDataService from '../../api/todo/TodoDataService.js';
import AuthentificationService from './AuthentificationService.js';

class ListTodosComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [],
            message: null
        }
        this.deleteTodoClicked = this.deleteTodoClicked.bind(this);
    }

    componentDidMount() {
        let username = AuthentificationService.getLoggedInUserName
        TodoDataService.retrieveAllTodos(username)
            .then(
                response => {
                    this.setState({ todos: response.data })
                }
            )

    }

    deleteTodoClicked(id) {
        let username = AuthentificationService.getLoggedInUserName()
        TodoDataService.deleteTodos(username, id)
            .then(
                response => {
                    this.setState({ message: `Delete of todo ${id + " " + this.state.description}` })
                }
            )
    }

    render() {
        return (

            <div>
                <h1>List Todos</h1>
                {this.state.message && <div className="alert alert_success">{this.state.message}</div>}
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>Description</th>
                                <th>Is Completed ?</th>
                                <th>Target Date</th>
                                <th>Delete</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.todos.map(
                                    todo =>
                                        <tr key={todo.id}>
                                            <td>{todo.description}</td>
                                            <td>{todo.done.toString()}</td>
                                            <td>{todo.targetDate.toString()}</td>
                                            <td><button className="btn btn-warning" onClick={() => this.deleteTodoClicked(todo.id)}>Delete</button></td>
                                        </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListTodosComponent
